// 더보기 버튼
function setupMoreButton(textContainer) {
  const textBox = textContainer.querySelector(".comment-place");
  if (!textBox) return;

  if (textBox.scrollHeight > textBox.clientHeight) {
    const buttonWrapper = document.createElement("div");
    buttonWrapper.classList.add("comment-buttons");

    const button = document.createElement("button");
    button.classList.add("more-see", "comment-div3");
    button.textContent = "더보기";

    button.addEventListener("click", () => {
      const isExpanded = textBox.classList.contains("expanded");
      if (isExpanded) {
        textBox.classList.remove("expanded");
        button.textContent = "더보기";
      } else {
        textBox.classList.add("expanded");
        button.textContent = "접기";
      }
    });

    buttonWrapper.appendChild(button);
    textContainer.appendChild(buttonWrapper);
  }
}

document.addEventListener("DOMContentLoaded", () => {
  document.querySelectorAll(".text").forEach(setupMoreButton);
});

// 등록 누르면 댓글 상세 페이지로 이동
function goTo() {
  const submitBtn = document.getElementById("submit-comment");
  submitBtn.addEventListener("click", ()=>{
    const inputEl = document.querySelector(".inputplace");
      if (!inputEl) {
        alert("입력창이 존재하지 않습니다.");
        return;
      }
    const inputText = inputEl.value.trim();
      if (inputText) {
        // 원하는 페이지로 이동
        window.location.href = "./community_detail_comment.html";
      } else {
        alert("내용을 입력해주세요!");
      }
  })
}


// 공유 버튼을 누르면 현재 페이지 url 복사
document.querySelectorAll(".copy-url-btn").forEach((btn) => {
  btn.addEventListener("click", () => {
    const currentUrl = window.location.href;

    // 클립보드에 복사
    navigator.clipboard
      .writeText(currentUrl)
      .then(() => {
        alert("주소가 복사되었습니다!");
      })
      .catch((err) => {
        console.error("주소 복사 실패:", err);
        alert("주소 복사에 실패했습니다.");
      });
  });
});

